       const image = async(req, res) => {
    let hotel = await Hotel.findById(req.params.hotelId).exec();
    if(hotel && hotel.image && hotel.image.data !== null ){
        res.set("Content-Type", hotel.image.contentType);
        return res.send(hotel.image.data)
    }
}

